mapper.select:
		select pcode,rcode, m.uname, rscore, rcommend, rimage, count(rscore) OVER() cnt, FLOOR(avg(rscore) OVER()) avge
 		  from review r join member m on r.ucode = m.ucode
 		 where pcode=#{pcode}

mapper.insert:

		SELECT IFNULL(MAX(PCODE), 0) + 1 PCODE
		FROM PRODUCT

	INSERT INTO PRODUCT 
	(
	    PCODE
	  , GCODE
	  ,	PNAME
	  , STOCK
	  , TITLE
	  , PRICE
	  , PCONTENT
	)
	VALUES 
	(
		#{pcode}
	  , #{groups}
      , #{pname}
	  , #{stock}
	  , #{title}
	  , #{price}
	  , #{content}

	)


	SELECT IFNULL(MAX(P_SEQ), 0) + 1 
	  FROM PIMAGE
	 WHERE PCODE = #{pcode}

INSERT INTO PIMAGE
(
	PCODE
  , P_SEQ	
  , PIMAGE
)
VALUES
(
	#{pcode}
  , #{pseq}
  , #{fileName}
)
