mapper.insert:
/** [Dp_SQL.xml][egovframework.example.dp.service.impl.DpMapper.saveBnnrData][배너목록][hanq] */
SELECT IFNULL(MAX(BNNR_MNG_NO), 0) + 1 bnnrMngNo FROM T_DP_BNNR_MNG
INSERT INTO T_DP_BNNR_MNG ( BNNR_MNG_NO , SUBJ , DP_STRT_DT , DP_END_DT , DP_YN , REGR , REG_DT , UPDR , UPD_DT ) VALUES ( #{bnnrMngNo} , #{subj} , #{dpStrtDt} , #{dpEndDt} , #{dpYn} , 'SYSTEM' , NOW() , 'SYSTEM' , NOW() )

/** [Dp_SQL.xml][egovframework.example.dp.service.impl.DpMapper.saveBnnrData][배너목록][hanq] */


		SELECT IFNULL(MAX(DTL_SEQ), 0) + 1 dtlSeq
			FROM T_DP_BNNR_DTL
		WHERE BNNR_MNG_NO = #{bnnrMngNo}


	INSERT INTO T_DP_BNNR_DTL
	(
		BNNR_MNG_NO
		, DTL_SEQ
		, IMG_ATTC_FILE
		, CNNT_URL
		, BNNR_TEXT
		, SORT_SEQ
		, REGR
		, REG_DT
		, UPDR
		, UPD_DT
	)
	VALUES
	(
		#{bnnrMngNo}
		, #{dtlSeq}
		, 1
		, #{cnntUrl}
		, #{bnnrText}
		, #{sortSeq}
		, 'SYSTEM'
		, NOW()
		, 'SYSTEM'
		, NOW()
	)

mapper.update:
		UPDATE T_DP_BNNR_MNG
           SET SUBJ       = #{subj}
             , DP_STRT_DT = #{dpStrtDt}
             , DP_END_DT  = #{dpEndDt}
             , DP_YN      = #{dpYn}
             , UPDR       = 'SYSTEM'
             , UPD_DT     = NOW()
         WHERE BNNR_MNG_NO = #{bnnrMngNo}

		UPDATE T_DP_BNNR_DTL
           SET IMG_ATTC_FILE	= 1
			, CNNT_URL		= #{cnntUrl}
			, BNNR_TEXT		= #{bnnrText}
			, SORT_SEQ		= #{sortSeq}
			, UPDR		 	= 'SYSTEM'
			, UPD_DT     	= NOW()
         WHERE BNNR_MNG_NO = #{bnnrMngNo}
           AND DTL_SEQ 		= #{dtlSeq}

mapper.insert:
/** [DP_SQL.xml][egovframework.example.dp.service.impl.DpMapper.insertGridRowData][배너목록][hanq] */


		SELECT IFNULL(MAX(DTL_SEQ), 0) + 1 dtlSeq
		  FROM T_DP_BNNR_DTL
		 WHERE BNNR_MNG_NO = #{bnnrMngNo}

	INSERT INTO T_DP_BNNR_DTL
	(
	    BNNR_MNG_NO	
	  , DTL_SEQ
	  , IMG_ATTC_FILE
	  , CNNT_URL
	  , BNNR_TEXT
	  , SORT_SEQ
	  , REGR
	  , REG_DT
	  , UPDR
	  , UPD_DT
	)
	VALUES
	(
	 	#{bnnrMngNo}
	  , #{dtlSeq}
	  , 2
	  , #{cnntUrl}
	  , #{bnnrText}
	  , #{sortSeq}
	  , 'SYSTEM'
	  , NOW()
	  , 'SYSTEM'
	  , NOW()
	)

mapper.update:
	/** [DP_SQL.xml][egovframework.example.dp.service.impl.DpMapper.updateGridRowData][배너목록][hanq] */

 	UPDATE T_DP_BNNR_DTL
 	   SET CNNT_URL		= #{cnnrUrl}
 	     , BNNR_TEXT	= #{bnnrText}
 	     , SORT_SEQ		= #{sortSeq}
 	     , UPDR			= 'SYSTEM'
 	     , UPD_DT		= NOW()
 	 WHERE DTL_SEQ		= #{dtlSeq} 
 	   AND BNNR_MNG_NO	= #{bnnrMngNo}

mapper.delete:

	DELETE 
 	  FROM T_DP_BNNR_DTL
 	 WHERE BNNR_MNG_NO = #{bnnrMngNo}
       AND DTL_SEQ     = #{dtlSeq}
